using Butterfly.HabboHotel.GameClients;namespace Butterfly.HabboHotel.Rooms.Chat.Commands.Cmd{    internal class Override : IChatCommand    {        public string PermissionRequired        {            get { return ""; }        }        public string Parameters        {            get { return ""; }        }        public string Description        {            get { return ""; }        }        public void Execute(GameClients.GameClient Session, Room Room, string[] Params)        {            if (UserRoom.AllowOverride)            {                UserRoom.AllowOverride = false;                UserRoom.SendWhisperChat(ButterflyEnvironment.GetLanguageManager().TryGetValue("override.disabled", Session.Langue));            }            else            {                UserRoom.AllowOverride = true;                UserRoom.SendWhisperChat(ButterflyEnvironment.GetLanguageManager().TryGetValue("override.enabled", Session.Langue));            }        }    }}